public String todoWrite(Todos todos) { // @formatter:on

	// Validate the todos
	validateTodos(todos);

	this.todoListConsumer.accept(todos);

	return "Todos have been modified successfully. Ensure that you continue to use the todo list to track your progress. Please proceed with the current tasks if applicable";
}

/**
 * Validates the todo list according to the following rules:
 * - Only one task can be in_progress at a time
 * - Task content and activeForm must not be empty or blank
 * - All tasks must have valid status values
 * @param todos the todo list to validate
 * @throws IllegalArgumentException if validation fails
 */
private void validateTodos(Todos todos) {
	if (todos == null || todos.todos() == null) {
		throw new IllegalArgumentException("Todos cannot be null");
	}

	List<TodoItem> items = todos.todos();

	// Count in_progress tasks
	long inProgressCount = items.stream()
		.filter(item -> item.status() == Todos.Status.in_progress)
		.count();

	if (inProgressCount > 1) {
		throw new IllegalArgumentException(
				"Only ONE task can be in_progress at a time. Found " + inProgressCount + " in_progress tasks. "
						+ "Please mark the current task as completed before starting a new one.");
	}

	// Validate each task
	for (int i = 0; i < items.size(); i++) {
		TodoItem item = items.get(i);

		if (item == null) {
			throw new IllegalArgumentException("Task at index " + i + " is null");
		}

		if (item.content() == null || item.content().isBlank()) {
			throw new IllegalArgumentException(
					"Task at index " + i + " has empty or blank content. All tasks must have meaningful content.");
		}

		if (item.activeForm() == null || item.activeForm().isBlank()) {
			throw new IllegalArgumentException("Task at index " + i + " has empty or blank activeForm. "
					+ "All tasks must have an activeForm (present continuous tense).");
		}

		if (item.status() == null) {
			throw new IllegalArgumentException(
					"Task at index " + i + " has null status. Status must be one of: pending, in_progress, completed");
		}
	}
}

